import {
  BadRequestException,
  ForbiddenException,
  Injectable,
  Logger,
  NotFoundException,
} from '@nestjs/common';
import { ShoppingListRepository } from '../repositories/shoppingList.repository';
import { CreateShoppingListDto } from '../dto/create-shoppingList.dto';
import {
  MultipleShoppingListResponseDto,
  ShoppingListResponseDto,
} from '../dto/shoppingList-response.dto';
import { plainToClass, plainToInstance } from 'class-transformer';
import { ShoppingListQueryDto } from '../dto/shoppingList-query.dto';
import { UpdateShoppingListDto } from '../dto/update.shoppingList.dto';

@Injectable()
export class ShoppingListService {
  private readonly logger = new Logger(ShoppingListService.name);

  constructor(
    private readonly shoppingListRepository: ShoppingListRepository,
  ) {}

  async create(
    createShoppingListDto: CreateShoppingListDto,
    userId: string,
  ): Promise<ShoppingListResponseDto> {
    this.logger.log(`Creating a shopping list: ${createShoppingListDto.title}`);

    try {
      const shoppingList = await this.shoppingListRepository.create({
        ...createShoppingListDto,
        userId,
      });
      return this.transformToResponseDto(shoppingList);
    } catch (error: any) {
      if (error.code === 'P2002') {
        throw new BadRequestException(
          'Shopping list with this title already exists',
        );
      }
      throw error;
    }
  }

  async findAll(
    query: ShoppingListQueryDto,
  ): Promise<MultipleShoppingListResponseDto> {
    this.logger.log(`Finding all shopping list `);

    const shoppingList = await this.shoppingListRepository.findAll();

    const transformedData = plainToInstance(
      ShoppingListResponseDto,
      shoppingList,
      { excludeExtraneousValues: true },
    );

    return {
      data: transformedData,
    };
  }

  async findById(
    id: string,
    userId?: string,
  ): Promise<ShoppingListResponseDto> {
    this.logger.log(`Finding shopping list with the id:` + id);

    const shoppingList = await this.shoppingListRepository.findById(id);

    if (!shoppingList) {
      throw new NotFoundException('Shopping list dosent exist');
    }

    if (shoppingList.userId !== userId) {
      throw new ForbiddenException('No premission');
    }
    return this.transformToResponseDto(shoppingList);
  }

  async update(
    id: string,
    updateShoppingListDto: UpdateShoppingListDto,
    userId?: string,
  ): Promise<ShoppingListResponseDto> {
    try {
      const existingList = await this.shoppingListRepository.findById(id);
      if (!existingList) {
        throw new NotFoundException('Shopping list not found');
      }
      if (existingList.userId !== userId) {
        throw new ForbiddenException('No premission');
      }

      const shoppingList = await this.shoppingListRepository.update(
        id,
        updateShoppingListDto,
      );
      return this.transformToResponseDto(shoppingList);
    } catch (error) {
      if (
        error instanceof NotFoundException ||
        error instanceof ForbiddenException
      ) {
        throw error;
      }
      throw new BadRequestException('Failed to update shopping list');
    }
  }

  async remove(id: string, userId?: string): Promise<void> {
    try {
      const existingList = await this.shoppingListRepository.findById(id);
      if (!existingList) {
        throw new NotFoundException('Shopping list not found');
      }
      if (existingList.userId !== userId) {
        throw new ForbiddenException('No premission');
      }
      await this.shoppingListRepository.delete(id);
    } catch (error) {
      if (
        error instanceof NotFoundException ||
        error instanceof ForbiddenException
      ) {
        throw error;
      }
      throw new BadRequestException('Failed to delete shopping list');
    }
  }

  private transformToResponseDto(shoppingList: any): ShoppingListResponseDto {
    return plainToClass(ShoppingListResponseDto, {
      id: shoppingList.id,
      title: shoppingList.title,
      createdBy: shoppingList.createdAt,
    });
  }
}

import { Test, TestingModule } from '@nestjs/testing';
import { PrismaService } from '../../database/prisma.service';
import { ShoppingListItemRepository } from '../repositories/shoppingListItem.repository';
import { CreateShoppingListItemDto } from '../dto/create-soppingListItem.dto';

describe('ShoppingListItemRepository', () => {
  let repository: ShoppingListItemRepository;
  let prismaService: PrismaService;

  const mockPrismaService = {
    shoppingListItem: {
      create: jest.fn(),
      findMany: jest.fn(),
      findUnique: jest.fn(),
      update: jest.fn(),
      delete: jest.fn(),
      deleteMany: jest.fn(),
      updateMany: jest.fn(),
      count: jest.fn(),
    },
    shoppingList: {
      count: jest.fn(),
    },
    food: {
      count: jest.fn(),
    },
  };

  const mockShoppingListItem = {
    id: '1',
    quantity: 2,
    unit: 'kg',
    notes: 'Test notes',
    checked: false,
    shoppingListId: 'list-1',
    foodId: 'food-1',
    createdAt: new Date('2024-01-01'),
    updatedAt: new Date('2024-01-01'),
    shoppingList: {
      id: 'list-1',
      title: 'Test List',
      userId: 'user-1',
    },
    food: {
      id: 'food-1',
      name: 'Test Food',
      category: 'Test Category',
      createdAt: new Date('2024-01-01'),
      updatedAt: new Date('2024-01-01'),
    },
  };

  beforeEach(async () => {
    const module: TestingModule = await Test.createTestingModule({
      providers: [
        ShoppingListItemRepository,
        {
          provide: PrismaService,
          useValue: mockPrismaService,
        },
      ],
    }).compile();

    repository = module.get<ShoppingListItemRepository>(
      ShoppingListItemRepository,
    );
    prismaService = module.get<PrismaService>(PrismaService);
  });

  afterEach(() => {
    jest.clearAllMocks();
  });

  describe('create', () => {
    it('should create a shopping list item with relations', async () => {
      // Arrange
      const createDto: CreateShoppingListItemDto = {
        quantity: 2,
        unit: 'kg',
        notes: 'Test notes',
        checked: false,
        shoppingListId: 'list-1',
        foodId: 'food-1',
      };

      mockPrismaService.shoppingListItem.create.mockResolvedValue(
        mockShoppingListItem,
      );

      // Act
      const result = await repository.create(createDto);

      // Assert
      expect(mockPrismaService.shoppingListItem.create).toHaveBeenCalledWith({
        data: createDto,
        include: {
          shoppingList: true,
          food: true,
        },
      });
      expect(result).toEqual(mockShoppingListItem);
      expect(result.shoppingListId).toBeDefined();
      expect(result.foodId).toBeDefined();
    });
  });

  describe('findMany', () => {
    it('should find items with basic filter', async () => {
      // Arrange
      const filter = { shoppingListId: 'list-1' };
      mockPrismaService.shoppingListItem.findMany.mockResolvedValue([
        mockShoppingListItem,
      ]);

      // Act
      const result = await repository.findMany(filter);

      // Assert
      expect(mockPrismaService.shoppingListItem.findMany).toHaveBeenCalledWith({
        where: { shoppingListId: 'list-1' },
        include: {
          shoppingList: true,
          food: true,
        },
        orderBy: [{ checked: 'asc' }, { createdAt: 'desc' }],
      });
      expect(result).toEqual([mockShoppingListItem]);
    });

    it('should find items with complex filter', async () => {
      // Arrange
      const filter = {
        shoppingListId: 'list-1',
        foodId: 'food-1',
        checked: false,
        unit: 'kg',
        userId: 'user-1',
      };
      mockPrismaService.shoppingListItem.findMany.mockResolvedValue([
        mockShoppingListItem,
      ]);

      // Act
      const result = await repository.findMany(filter);

      // Assert
      expect(mockPrismaService.shoppingListItem.findMany).toHaveBeenCalledWith({
        where: {
          shoppingListId: 'list-1',
          foodId: 'food-1',
          checked: false,
          unit: { contains: 'kg', mode: 'insensitive' },
          shoppingList: { userId: 'user-1' },
        },
        include: {
          shoppingList: true,
          food: true,
        },
        orderBy: [{ checked: 'asc' }, { createdAt: 'desc' }],
      });
      expect(result).toEqual([mockShoppingListItem]);
    });

    it('should find items with empty filter', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.findMany.mockResolvedValue([
        mockShoppingListItem,
      ]);

      // Act
      const result = await repository.findMany();

      // Assert
      expect(mockPrismaService.shoppingListItem.findMany).toHaveBeenCalledWith({
        where: {},
        include: {
          shoppingList: true,
          food: true,
        },
        orderBy: [{ checked: 'asc' }, { createdAt: 'desc' }],
      });
      expect(result).toEqual([mockShoppingListItem]);
    });
  });

  describe('findById', () => {
    it('should find item by id', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.findUnique.mockResolvedValue(
        mockShoppingListItem,
      );

      // Act
      const result = await repository.findById('1');

      // Assert
      expect(
        mockPrismaService.shoppingListItem.findUnique,
      ).toHaveBeenCalledWith({
        where: { id: '1' },
        include: {
          shoppingList: true,
          food: true,
        },
      });
      expect(result).toEqual(mockShoppingListItem);
    });

    it('should return null if item not found', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.findUnique.mockResolvedValue(null);

      // Act
      const result = await repository.findById('non-existent');

      // Assert
      expect(result).toBeNull();
    });
  });

  describe('findByShoppingListAndFood', () => {
    it('should find item by shopping list and food', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.findUnique.mockResolvedValue(
        mockShoppingListItem,
      );

      // Act
      const result = await repository.findByShoppingListAndFood(
        'list-1',
        'food-1',
      );

      // Assert
      expect(
        mockPrismaService.shoppingListItem.findUnique,
      ).toHaveBeenCalledWith({
        where: {
          shoppingListId_foodId: {
            shoppingListId: 'list-1',
            foodId: 'food-1',
          },
        },
        include: {
          shoppingList: true,
          food: true,
        },
      });
      expect(result).toEqual(mockShoppingListItem);
    });
  });

  describe('update', () => {
    it('should update item', async () => {
      // Arrange
      const updateData = { quantity: 5, checked: true };
      const updatedItem = { ...mockShoppingListItem, ...updateData };
      mockPrismaService.shoppingListItem.update.mockResolvedValue(updatedItem);

      // Act
      const result = await repository.update('1', updateData);

      // Assert
      expect(mockPrismaService.shoppingListItem.update).toHaveBeenCalledWith({
        where: { id: '1' },
        data: updateData,
        include: {
          shoppingList: true,
          food: true,
        },
      });
      expect(result).toEqual(updatedItem);
    });
  });

  describe('toggleChecked', () => {
    it('should toggle checked status', async () => {
      // Arrange
      const currentItem = { checked: false };
      const toggledItem = { ...mockShoppingListItem, checked: true };

      mockPrismaService.shoppingListItem.findUnique.mockResolvedValue(
        currentItem,
      );
      mockPrismaService.shoppingListItem.update.mockResolvedValue(toggledItem);

      // Act
      const result = await repository.toggleChecked('1');

      // Assert
      expect(
        mockPrismaService.shoppingListItem.findUnique,
      ).toHaveBeenCalledWith({
        where: { id: '1' },
        select: { checked: true },
      });
      expect(mockPrismaService.shoppingListItem.update).toHaveBeenCalledWith({
        where: { id: '1' },
        data: { checked: true },
        include: {
          shoppingList: true,
          food: true,
        },
      });
      expect(result).toEqual(toggledItem);
    });

    it('should throw error if item not found during toggle', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.findUnique.mockResolvedValue(null);

      // Act & Assert
      await expect(repository.toggleChecked('non-existent')).rejects.toThrow(
        'Shopping list item not found',
      );
    });
  });

  describe('delete', () => {
    it('should delete item', async () => {
      // Arrange
      mockPrismaService.shoppingListItem.delete.mockResolvedValue(
        mockShoppingListItem,
      );

      // Act
      await repository.delete('1');

      // Assert
      expect(mockPrismaService.shoppingListItem.delete).toHaveBeenCalledWith({
        where: { id: '1' },
      });
    });
  });

  describe('clearCheckedItems', () => {
    it('should clear checked items for shopping list', async () => {
      // Arrange
      const deleteResult = { count: 3 };
      mockPrismaService.shoppingListItem.deleteMany.mockResolvedValue(
        deleteResult,
      );

      // Act
      const result = await repository.clearCheckedItems('list-1', 'user-1');

      // Assert
      expect(
        mockPrismaService.shoppingListItem.deleteMany,
      ).toHaveBeenCalledWith({
        where: {
          shoppingListId: 'list-1',
          checked: true,
          shoppingList: { userId: 'user-1' },
        },
      });
      expect(result).toEqual(deleteResult);
    });

    it('should clear checked items without user filter', async () => {
      // Arrange
      const deleteResult = { count: 2 };
      mockPrismaService.shoppingListItem.deleteMany.mockResolvedValue(
        deleteResult,
      );

      // Act
      const result = await repository.clearCheckedItems('list-1');

      // Assert
      expect(
        mockPrismaService.shoppingListItem.deleteMany,
      ).toHaveBeenCalledWith({
        where: {
          shoppingListId: 'list-1',
          checked: true,
        },
      });
      expect(result).toEqual(deleteResult);
    });
  });

  describe('count', () => {
    it('should count items with filter', async () => {
      // Arrange
      const filter = { shoppingListId: 'list-1', checked: false };
      mockPrismaService.shoppingListItem.count.mockResolvedValue(5);

      // Act
      const result = await repository.count(filter);

      // Assert
      expect(mockPrismaService.shoppingListItem.count).toHaveBeenCalledWith({
        where: {
          shoppingListId: 'list-1',
          checked: false,
        },
      });
      expect(result).toBe(5);
    });
  });
});

import { Injectable } from '@nestjs/common';
import { Prisma, ShoppingListItem } from '@prisma/client';
import { PrismaService } from '../../database/prisma.service';
import { CreateShoppingListItemDto } from '../dto/create-soppingListItem.dto';
import { UpdateShoppingListItemDto } from '../dto/update-soppingListItem.dto';
import {
  BaseRepository,
  FindAllOptions,
} from 'src/common/interfaces/base-repository.interface';

export type ShoppingListItemWithRelations = ShoppingListItem & {
  shoppingList: {
    id: string;
    title: string;
    userId: string;
  };
  food: {
    id: string;
    name: string;
    category?: string;
    createdAt: Date;
    updatedAt: Date;
  };
};

export interface ShoppingListItemFilter {
  shoppingListId?: string;
  foodId?: string;
  checked?: boolean;
  unit?: string;
  userId?: string;
}

@Injectable()
export class ShoppingListItemRepository
  implements
    BaseRepository<
      ShoppingListItem,
      CreateShoppingListItemDto,
      UpdateShoppingListItemDto
    >
{
  constructor(private readonly prisma: PrismaService) {}

  async create(
    data: CreateShoppingListItemDto,
  ): Promise<ShoppingListItemWithRelations> {
    return this.prisma.shoppingListItem.create({
      data,
      include: {
        shoppingList: true,
        food: true,
      },
    });
  }

  findAll(options?: FindAllOptions): Promise<
    {
      id: string;
      quantity: number;
      unit: string;
      notes: string | null;
      checked: boolean;
      shoppingListId: string;
      foodId: string;
      createdAt: Date;
      updatedAt: Date;
    }[]
  > {
    throw new Error('Method not implemented.');
  }

  async findMany(
    filter: ShoppingListItemFilter = {},
  ): Promise<ShoppingListItemWithRelations[]> {
    return this.prisma.shoppingListItem.findMany({
      where: {
        shoppingListId: filter.shoppingListId,
        foodId: filter.foodId,
        checked: filter.checked,
        unit: filter.unit
          ? { contains: filter.unit, mode: 'insensitive' }
          : undefined,

        shoppingList: filter.userId ? { userId: filter.userId } : undefined,
      },
      include: {
        shoppingList: true,
        food: true,
      },
      orderBy: [{ checked: 'asc' }, { createdAt: 'desc' }],
    });
  }

  async findByShoppingListId(
    shoppingListId: string,
    userId?: string,
  ): Promise<ShoppingListItemWithRelations[]> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {
      shoppingListId,
    };

    // Add user access control if userId provided
    if (userId) {
      whereConditions.shoppingList = {
        userId,
      };
    }

    return this.prisma.shoppingListItem.findMany({
      where: whereConditions,
      include: {
        shoppingList: true,
        food: true,
      },
      orderBy: [
        { checked: 'asc' }, // Unchecked items first
        { createdAt: 'desc' },
      ],
    });
  }

  async findById(id: string): Promise<ShoppingListItemWithRelations | null> {
    return this.prisma.shoppingListItem.findUnique({
      where: { id },
      include: {
        shoppingList: true,
        food: true,
      },
    });
  }

  async findByShoppingListAndFood(
    shoppingListId: string,
    foodId: string,
  ): Promise<ShoppingListItemWithRelations | null> {
    return this.prisma.shoppingListItem.findUnique({
      where: {
        shoppingListId_foodId: {
          shoppingListId,
          foodId,
        },
      },
      include: {
        shoppingList: true,
        food: true,
      },
    });
  }

  async update(
    id: string,
    data: Prisma.ShoppingListItemUpdateInput,
  ): Promise<ShoppingListItemWithRelations> {
    return this.prisma.shoppingListItem.update({
      where: { id },
      data,
      include: {
        shoppingList: true,
        food: true,
      },
    });
  }

  async delete(id: string): Promise<void> {
    await this.prisma.shoppingListItem.delete({
      where: { id },
    });
  }

  async deleteMany(filter: ShoppingListItemFilter): Promise<{ count: number }> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {};

    if (filter.shoppingListId) {
      whereConditions.shoppingListId = filter.shoppingListId;
    }

    if (filter.checked !== undefined) {
      whereConditions.checked = filter.checked;
    }

    if (filter.userId) {
      whereConditions.shoppingList = {
        userId: filter.userId,
      };
    }

    return this.prisma.shoppingListItem.deleteMany({
      where: whereConditions,
    });
  }

  async count(filter: ShoppingListItemFilter = {}): Promise<number> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {};

    if (filter.shoppingListId) {
      whereConditions.shoppingListId = filter.shoppingListId;
    }

    if (filter.foodId) {
      whereConditions.foodId = filter.foodId;
    }

    if (filter.checked !== undefined) {
      whereConditions.checked = filter.checked;
    }

    if (filter.userId) {
      whereConditions.shoppingList = {
        userId: filter.userId,
      };
    }

    return this.prisma.shoppingListItem.count({
      where: whereConditions,
    });
  }

  async toggleChecked(id: string): Promise<ShoppingListItemWithRelations> {
    // First get the current checked state
    const currentItem = await this.prisma.shoppingListItem.findUnique({
      where: { id },
      select: { checked: true },
    });

    if (!currentItem) {
      throw new Error('Shopping list item not found');
    }

    return this.prisma.shoppingListItem.update({
      where: { id },
      data: {
        checked: !currentItem.checked,
      },
      include: {
        shoppingList: true,
        food: true,
      },
    });
  }

  async clearCheckedItems(
    shoppingListId: string,
    userId?: string,
  ): Promise<{ count: number }> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {
      shoppingListId,
      checked: true,
    };

    if (userId) {
      whereConditions.shoppingList = {
        userId,
      };
    }

    return this.prisma.shoppingListItem.deleteMany({
      where: whereConditions,
    });
  }

  async markAllAsChecked(
    shoppingListId: string,
    userId?: string,
  ): Promise<{ count: number }> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {
      shoppingListId,
      checked: false,
    };

    if (userId) {
      whereConditions.shoppingList = {
        userId,
      };
    }

    return this.prisma.shoppingListItem.updateMany({
      where: whereConditions,
      data: {
        checked: true,
      },
    });
  }

  async markAllAsUnchecked(
    shoppingListId: string,
    userId?: string,
  ): Promise<{ count: number }> {
    const whereConditions: Prisma.ShoppingListItemWhereInput = {
      shoppingListId,
      checked: true,
    };

    if (userId) {
      whereConditions.shoppingList = {
        userId,
      };
    }

    return this.prisma.shoppingListItem.updateMany({
      where: whereConditions,
      data: {
        checked: false,
      },
    });
  }

  async validateShoppingListExists(
    shoppingListId: string,
    userId: string,
  ): Promise<boolean> {
    const count = await this.prisma.shoppingList.count({
      where: {
        id: shoppingListId,
        userId: userId,
      },
    });
    return count > 0;
  }

  async validateFoodExists(foodId: string): Promise<boolean> {
    const count = await this.prisma.food.count({
      where: { id: foodId },
    });
    return count > 0;
  }
}

